            <script>
                $(document).ready(function () {
                    // Load the initial data from local storage if available
                    const storedData1 = localStorage.getItem('data1');
                    const storedData2 = localStorage.getItem('data2');
                    const storedData3 = localStorage.getItem('data3');
                    const storedData1md = localStorage.getItem('data1md');
                    const storedData2md = localStorage.getItem('data2md');
                    const storedData3md = localStorage.getItem('data3md');

                    if (storedData1) {
                        $('#data1').html(storedData1);
                        $('#data1md').html(storedData1md);
                    }
                    if (storedData2) {
                        $('#data2').html(storedData2);
                        $('#data2md').html(storedData2md);
                    }
                    if (storedData3) {
                        $('#data3').html(storedData3);
                        $('#data3md').html(storedData3md);
                    }

                    setInterval(function () {
                        getDataRuang();
                    }, 200);
                });

                function getDataRuang() {
                    $.ajax({
                        url: "ruang_guru.php",
                        type: "GET",
                        success: function (response) {
                            $('#data1').html(response);
                            $('#data1md').html(response);
                            // Store the new data in local storage
                            localStorage.setItem('data1', response);
                            localStorage.setItem('data1md', response);
                        }
                    });

                    $.ajax({
                        url: "lab_rpl_a.php",
                        type: "GET",
                        success: function (response) {
                            $('#data2').html(response);
                            $('#data2md').html(response);
                            // Store the new data in local storage
                            localStorage.setItem('data2', response);
                            localStorage.setItem('data2md', response);
                        }
                    });

                    $.ajax({
                        url: "lab_rpl_b.php",
                        type: "GET",
                        success: function (response) {
                            $('#data3').html(response);
                            $('#data3md').html(response);
                            // Store the new data in local storage
                            localStorage.setItem('data3', response);
                            localStorage.setItem('data3md', response);
                        }
                    });
                }
            </script>

            <script>
                $('document').ready(function () {
                    // Load the initial data from local storage if available
                    const storedData = JSON.parse(localStorage.getItem('logData'));
                    if (storedData) {
                        populateTable(storedData);
                    }

                    setInterval(function () {
                        getLog();
                    }, 200);
                });

                function getLog() {
                    $.ajax({
                        url: "realtime.php",
                        type: "GET",
                        success: function (response) {
                            // Update the table with new data
                            $('#log').html(response);
                            // Store the new data in local storage
                            localStorage.setItem('logData', JSON.stringify(response));
                        }
                    });
                }

                function populateTable(data) {
                    // Populate the table with data
                    $('#log').html(data);
                }
            </script>
